mapper.select:
SELECT r.*, a.name AS writerName, a.role AS writerRole, (SELECT COUNT(*) FROM dev_comments c WHERE c.req_id = r.req_id AND c.del_yn = 'N') AS commentCount FROM dev_requests r JOIN admins a ON r.admin_id = a.admin_id
AND (r.title LIKE CONCAT('%', #{keyword}, '%') OR a.name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY r.urgency DESC, r.req_id DESC LIMIT #{amount} OFFSET ${(pageNum - 1) * amount}

mapper.insert:
INSERT INTO dev_requests (
    admin_id, category, urgency, title, content, status, due_date, created_at, updated_at
) VALUES (
             #{adminId}, #{category}, IFNULL(#{urgency}, 'N'), #{title}, #{content}, 'WAITING', #{dueDate}, NOW(), NOW()
         )
