mapper.update:
UPDATE members
SET last_login_at = NOW()
WHERE member_id = #{memberId}

UPDATE members
SET
nickname = #{nickname},
updated_at = NOW()

    , profile_image = #{profileImage}

WHERE member_id = #{memberId}

mapper.select:
SELECT expires_at FROM auth_codes
WHERE phone_number = #{phoneNumber}
ORDER BY auth_id DESC LIMIT 1

SELECT email, nickname
FROM members
WHERE birthdate = #{birthdate}
  AND phone_number = #{phoneNumber}
  AND status = 'ACTIVE'

SELECT * FROM members
WHERE nickname = #{userName}  AND phone_number = #{phoneNumber}
  AND status = 'ACTIVE'

SELECT * FROM members
WHERE email = #{email}
  AND phone_number = #{phoneNumber}
  AND status = 'ACTIVE'

mapper.update:
UPDATE members
SET fcm_token = #{token}
WHERE member_id = #{memberId}

mapper.select:
SELECT COUNT(*)
FROM follows
WHERE follower_id = #{followerId}
  AND following_id = #{followingId}

SELECT * FROM members
WHERE social_provider = #{socialProvider}
  AND social_uid = #{socialUid}
